var Convert = function(){

}

Convert.prototype.change_encoding = function(s, enc, to_enc){
    var r = s;
    try {
        var Iconv = require('iconv').Iconv;
        var conv = new Iconv(enc, to_enc);
        r = conv.convert(s);
    } catch(ce) {}

    return r;
}

module.exports = new Convert();